import {useState} from "react";
import { Modal } from 'react-bootstrap';
import 'react-daterange-picker/dist/css/react-calendar.css';
import 'react-date-range/dist/styles.css';
import 'react-date-range/dist/theme/default.css';
import { DateRangePicker } from 'react-date-range';

export default function AddBlog(){
	const [toneModal, setToneModal] = useState(false);
	const TonehandleClose = () =>{
		setToneModal(false);
	}
	const OpenToneModal = () =>{
		setToneModal(true);
	}
	const [showPicker, setShowPicker] = useState(false);
	const [dateRange, setDateRange] = useState([
	{
		startDate: new Date(),
		endDate: new Date(),
		key: 'selection',
	},
	]);
	const toggleDatePicker = () => {
		setShowPicker(!showPicker);
	};
	const handleDateSelect = (ranges) => {
		setDateRange([ranges.selection]);
	};
	const handleSubmit = async (event) => {
		event.preventDefault();
		const apiUrl = `${import.meta.env.VITE_API_BASE_URL}/api/blog/posting`
		// document.getElementById("browse").innerHTML = `<span class="spinner-border spinner-border-sm" role="status" aria-hidden="true" style="padding: 10px; margin: 0px 20px;"></span>`;
		const form = document.querySelector('#ajaForm');
        const data = new FormData(form);
		// var mlength = data.get('mlength');
		// var mtopic = data.get('mtopic');
		// var mtone = data.get('mtone');
		// var mfkw = data.get('mfkw');
		// if(mlength == ""){ mlength = 1000; }
		// if(mtone == ""){ mtone = "Formal, Professional"; }
		// if(mfkw == ""){ mfkw = mtopic; }
		// data.append("mtopic", data.get('mtopic'));
		// data.append("mlength", mlength);
		// data.append("mtone", mtone);
		// data.append("mfkw", mfkw);
		// data.append("mfvol", data.get('mfvol'));
		// data.append("mfreq", data.get('mfreq'));
		// data.append("daterange", data.get('daterange'));
		// data.append("mftime", data.get('mftime'));
		// data.append("fwmon", data.get('fwmon'));
		// data.append("fwtue", data.get('fwtue'));
		// data.append("fwwed", data.get('fwwed'));
		// data.append("fwthrs", data.get('fwthrs'));
		// data.append("fwfri", data.get('fwfri'));
		// data.append("fwsat", data.get('fwsat'));
		// data.append("fwsun", data.get('fwsun'));
        // form.reset()
		// fetch(apiUrl, {
		// 	method: "POST", 
		// 	body:data
		// 	}).then(res=>res.json())
		// 	.then((result)=>{
		// 		console.log("Here is the result: "+data);
		// 		alert(result);
		// 	// this.refreshTesting();
		// 	})
			const response2 = await fetch(apiUrl, {
				method: 'POST',
				body:data
			})
			if (response2.ok) {
				const data = await response2.json();
				const parsedData = data ;
				const blogerror = document.getElementById("blogsucccess");
				blogerror.style.display = "block";
				blogerror.innerHTML = "Blog has been added and scheduled successfully.";
				const closeModalButton = document.getElementById("closemodal");
				closeModalButton.click();
				console.log("THE RESPONSE IS: "+parsedData);
				document.getElementById("browse").innerHTML = `Generate`;
			}
		else {
			const err = await response2.text();
			const blogerror = document.getElementById("blogerror");
			blogerror.style.display = "block";
			blogerror.innerHTML = err;
			document.getElementById("browse").innerHTML = `Generate`;
			// alert(err)
		}
	};
	return (
		<div>
			<h4 className="fw-bold py-3 mb-4"><span className="text-muted fw-light">Blog /</span> Add</h4> 

			<form action="{{route('admin.blog.save')}}" method="post" className="ajaxForm">
				{/* <input type="hidden" name="id" value="{{$edit->hashid ?? null}}"> */}
				<div className="row reverse_div">
					<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 quater" id="complete_text_dev" style={{display:"block"}}>
						<div className="card radius-15">
							<div className="card-body">
							<div className="card-title" style={{display: "flex", justifyContent: "space-between"}}>
								<h4 className="mb-0">Add Blog</h4>
								<a type="button" className="btn btn-info" onClick={OpenToneModal} style={{backgroundColor: "#673ab7",borderColor: "#673ab7", marginTop: "-10px", color: "#fff"}}>Auto AI Generate</a>
							</div>
								<hr />
								<div className="row">
									<div className="col-12 col-lg-12">
										<div style={{backgroundColor: "#f8d7da", border: "1px solid #f5c2c7", padding: "1rem 1rem",marginBottom: "1rem", borderRadius: "0.25rem", color: "#842029", display: "none", marginTop: "-20px"}} id="blogerror">
										</div>
										<div style={{backgroundColor: "#d1e7dd", border: "1px solid #a3cfbb", padding: "1rem 1rem",marginBottom: "1rem", borderRadius: "0.25rem", color: "#0a3622", display: "none", marginTop: "-20px"}} id="blogsucccess">
										</div>
										<br />
									</div>
								</div>
								<div className="row">
									<div className="form-group col-lg-12 col-md-4  mb-2 mt-3">
										<label htmlFor="template_name">Title</label>
										<input type="text" className="form-control" id="title" name="title"
											required />
									</div>
									<div className="form-group col-lg-12 col-md-12 mb-2 mt-3">
										<label htmlFor="">Meta Description</label>
										<textarea name="meta_description" className="form-control" cols="30"
											rows="4" required></textarea>
									</div>
									<div className="form-group col-lg-12 col-md-12 mb-2 mt-3">
										<label htmlFor="">Description</label>
										<div id="snow-editor"></div>
										<input type="hidden" name="description" id="description" style={{height: "300px"}} />
									</div>

									<div className="form-group col-lg-6 col-md-6 mb-2" style={{marginTop: "90px;"}}>
										<label htmlFor="template_status">Author</label>
										<input type="text" className="form-control" name="auther" placeholder="Author" required />
									</div>
									<h4 style={{marginTop: "30px"}}>Schedule (Select if to post on particular time)</h4>
									<div className="form-group col-lg-6 col-md-6 mb-2">
										<label htmlFor="template_status">Select Date</label>
										<input type="date" className="form-control" name="scheduledate" required />
									</div>
									<div className="form-group col-lg-6 col-md-6 mb-2">
										<label htmlFor="template_status">Set Time</label>
										<input type="time" className="form-control" name="scheduletime" required />
									</div>
									<div className="form-group col-lg-4 col-md-4 mb-2 mt-3">
										<label htmlFor="" className="mb-3">Image/Icon <span className="text-danger">(size 500x500)</span></label>
										<br />
										<img className="profile-pic upload-button" src="https://via.placeholder.com/150/?text=upload%20icon"
											style={{width:"200px",height:"200px",cursor: "pointer"}} />
										<i className="lni lni-circle-plus"
											style={{fontSize: "19px", position: "absolute", marginLeft: "-18px", backgroundColor: "#f5f1f0", color: "black "}}></i>
										<div className="p-image">
											<input className="file-upload" name="image" type="file" accept="image/*"
												style={{display: "none"}} />
										</div>
									</div>
									<div className="form-group col-lg-12 col-md-12">
										<button type="sumbit" className="btn btn-xs btn-secondary" style={{backgroundColor: "#673ab7",borderColor: "#673ab7"}}>Publish</button>&nbsp; 
										<button type="button" className="btn btn-xs btn-secondary"
										id="draft_btn">Save as draft</button>
									</div>
								</div>
							</div>

						</div>
					</div>
				</div>
			</form>
			<Modal show={toneModal} centered onHide={TonehandleClose} data-bs-backdrop="static" data-bs-keyboard="false" aria-labelledby="staticBackdropLabel" aria-hidden="true" className="modal-lg">
			<Modal.Header>
                <h5 className="modal-title" id="modalToggleLabel">Auto Generate</h5>
				<button id="closemodal" type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"
					onClick={TonehandleClose}
				></button>
            </Modal.Header>
				<form onSubmit={handleSubmit} id="ajaForm" method="POST">
				<Modal.Body>
								<div className="row">
									<div className="col-lg-9 mb-3">
										<label htmlFor="ftopic" className="form-label">Content Topic</label>
										<input type="text" name="mtopic" id="ftopic" className="form-control" placeholder="Enter Blog Content Topic" required />
									</div>
									<div className="col-lg-3 mb-0">
										<label htmlFor="flength" className="form-label">Length</label>
										<input name="mlength"
										type="text"
										id="flength"
										className="form-control"
										placeholder="e.g. 1100"
										/>
									</div>
								</div>
								<div className="row g-2">
									<div className="col-lg-6 mb-0">
										<label htmlFor="fwtone" className="form-label">Writing Tone</label>
										<input name="mtone"
										type="text"
										id="fwtone"
										className="form-control"
										placeholder="e.g. Formal, Professional"
										/>
									</div>
									<div className="col-lg-6 mb-0">
										<label htmlFor="fwtone" className="form-label">Focus Keywords</label>
										<input name="mfkw"
										type="text"
										id="fwtone"
										className="form-control"
										placeholder="Enter focus keywords separated by comma's"
										/>
									</div>
								</div>
								<div className="row g-2 mt-1">
									<div className="col-lg-6 mb-0">
										<label htmlFor="fwvolume" className="form-label">Enter Volume</label>
										<input name="mfvol"
										type="text"
										id="fwvolume"
										className="form-control"
										placeholder="e.g. 1 / 2 / 3"
										/>
									</div>
									<div className="col-lg-6 mb-0">
										<label htmlFor="fwfrq" className="form-label">Enter Frequency</label>
										<div className="input-group">
											<select className="form-select" id="inputGroupSelect02" name="mfreq">
											<option value="daily" selected>Daily</option>
											<option value="weekly">Weekly</option>
											<option value="monthly">Monthly</option>
											</select>
											<label className="input-group-text" htmlFor="inputGroupSelect02">Frequency</label>
										</div>
									</div>
								</div>
								<div className="row g-2 mt-1">
									<div className="col-lg-8 mb-0">
										<label htmlFor="fwtime" className="form-label">Enter Posting Range</label>
										<input name="daterange" id="daterange" className="form-control input-daterange" style={{width: "100%"}} type="text" placeholder="From Date" onClick={toggleDatePicker} value={ dateRange[0].startDate.toDateString() + ' - ' + dateRange[0].endDate.toDateString() } required />
									</div>
									<div className="col-lg-4 mb-0">
										<label htmlFor="fwtime" className="form-label">Enter Posting Time</label>
										<input type="time" className="form-control" name="mftime" id="mftime" required  />
									</div>
									<div className="col-lg-10">
										{showPicker && (
											<div className="date-picker-popup" style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)"}}>
											<DateRangePicker
												ranges={dateRange}
												onChange={handleDateSelect}
												rangeColors={['#673ab7']} // Customize the selection color
												editableDateInputs={true}
												moveRangeOnFirstSelection={false}
											/>
											<button type="button" className="btn-close" aria-label="Close" onClick={() => setShowPicker(false)} style={{paddingTop: "2px", fontSize: "15px", outline: "none", marginLeft: "-10px", border: "1px solid #673ab7"}}></button>
											</div>
										)}
									</div>
								</div>
								<div className="row g-2 mt-1">
									<div className="col-lg-12 mb-0">
										<label htmlFor="fwpday" className="form-label">Posting Day</label>
									</div>
									<div className="row" style={{marginTop: "-6px"}}>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwmon" value="1" name="fwmon" />
												<label className="form-check-label" htmlFor="fwmon">Monday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwtue" value="1" name="fwtue" />
												<label className="form-check-label" htmlFor="fwtue">Tuesday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwwed" value="1" name="fwwed" />
												<label className="form-check-label" htmlFor="fwwed">Wednesday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwthrs" value="1" name="fwthrs" />
												<label className="form-check-label" htmlFor="fwthrs">Thursday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwfri" value="1" name="fwfri" />
												<label className="form-check-label" htmlFor="fwfri">Friday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwsat" value="1" name="fwsat" />
												<label className="form-check-label" htmlFor="fwsat">Saturday</label>
											</div>
										</div>
										<div className="col-lg-3">
											<div className="form-check form-check-inline">
												<input className="form-check-input" type="checkbox" id="fwsun" value="1" name="fwsun" />
												<label className="form-check-label" htmlFor="fwsun">Sunday</label>
											</div>
										</div>
									</div>
								</div>
				</Modal.Body>
				<Modal.Footer>
					<button type="submit" className="btn btn-primary" id="browse">Generate</button>
				</Modal.Footer>
				</form>
			</Modal>
		</div>
	)
}
